import de.dfki.introduction.IntroAgent.StatE;

void checkProp() {
  da = lastDA();	
  prop = da.getProposition();
  if (prop == "top") {
    if (situation == null) return;
    prop = situation.toString();
    prop = prop.substring(3).toLowerCase();
  }  
  if (prop == "name") prop = "givenname";
  prop="<dom:" + prop + ">";
  val = da.getValue("value");
  logger.info("given: {} is {}", prop, val);
  user.{prop} = val;
  lastDAprocessed();	
}

